import React, { useState, useEffect } from "react";
import "./css/style.css";

const Temp = () => {
  const [city, setCity] = useState(null);
  const [search, setSearch] = useState("mumbai");
  useEffect(() => {
    const fetchApi = async () => {
      const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=b14425a6554d189a2d7dc18a8e7d7263`;
      const response = await fetch(url);
      const resJson = await response.json();
      //   console.log(resJson);
      setCity(resJson.main);
    };
    fetchApi();
  }, [search]);
  return (
    <div>
      <div className='box'>
        <div className='wave -one'></div>
        <div className='wave -two'></div>
        <div className='wave -three'></div>

        <div>
          <input
            type='search'
            value={search}
            onChange={(event) => {
              setSearch(event.target.value);
            }}
          />
        </div>
        {!city ? (
          <p>No Data Found</p>
        ) : (
          <div>
            <h2>{search}</h2>
            <h3>{city.temp} Cel</h3>
            <h4>
              Min:{city.temp_min} Cel | Max : {city.temp_max} Cel
            </h4>
          </div>
        )}
      </div>
      EDIT ON
      <div class='box'>
        <div class='wave -one'></div>
        <div class='wave -two'></div>
        <div class='wave -three'></div>
        <div class='weathercon'>
          <i class='fas fa-sun' style='color: #d36326;'></i>
        </div>
        <div class='info'>
          <h2 class='location'>SUNNYVILLE</h2>
          <p class='date'>MONDAY | SEP 12 | 12:34</p>
          <h1 class='temp'>25 &deg;C | 77 &deg;F</h1>
        </div>
      </div>
    </div>
  );
};

export default Temp;
